import FoodList from "./FoodList";
import { getFoods } from "./api";
import { useEffect, useState } from "react";

const LIMIT = 10;

function App() {
  // 최상위 컴포넌트
  const [order, setOrder] = useState("createdAt");
  const [items, setItems] = useState([]);
  const [cursor, setCursor] = useState("");
  const [hasNext, setHasNext] = useState(false);

  const handleNewClick = () => setOrder("createdAt");
  const handleCalorieClick = () => setOrder("calorie");

  const handleDelete = (id) => {
    const nextItems = items.filter((item) => item.id !== id);
    setItems(nextItems);
  };

  const handleLoad = async (options) => {
    const { foods, paging } = await getFoods(options);
    if (options.cursor === "") {
      setItems(foods);
    } else {
      setItems((previousItems) => [...previousItems, ...foods]);
    }
    setCursor(options.cursor);
    setHasNext(paging.hasNext);
  };

  const handleLoadMore = () => {
    handleLoad({ order, cursor, limit: LIMIT });
  };

  useEffect(() => {
    handleLoad({ order, cursor: "", limit: LIMIT });
  }, [order]);

  return (
    <div>
      <div>
        <button onClick={handleNewClick}>최신순</button>
        <button onClick={handleCalorieClick}>칼로리순</button>
      </div>
      <FoodList items={items} onDelete={handleDelete} />
      {setHasNext && <button onClick={handleLoadMore}>더보기</button>}
    </div>
  );
}

export default App;
